import{_ as p,r as t,o as h,c as n,a as e,b as r,d as o,e as a}from"./app.34258611.js";const l={},d=o('<h1 id="\u6570\u636E\u5E93" tabindex="-1"><a class="header-anchor" href="#\u6570\u636E\u5E93" aria-hidden="true">#</a> \u6570\u636E\u5E93</h1><h2 id="\u5173\u7CFB\u578B\u6570\u636E\u5E93" tabindex="-1"><a class="header-anchor" href="#\u5173\u7CFB\u578B\u6570\u636E\u5E93" aria-hidden="true">#</a> \u5173\u7CFB\u578B\u6570\u636E\u5E93</h2><p>\u5173\u7CFB\u578B\u6570\u636E\u5E93\u7279\u70B9\uFF1A</p><ol><li><p>\u5B58\u50A8\u65B9\u5F0F\uFF1A\u4F20\u7EDF\u7684\u5173\u7CFB\u578B\u6570\u636E\u5E93\u91C7\u7528\u8868\u683C\u7684\u50A8\u5B58\u65B9\u5F0F\uFF0C\u6570\u636E\u4EE5\u884C\u548C\u5217\u7684\u65B9\u5F0F\u8FDB\u884C\u5B58\u50A8\uFF0C\u8981\u8BFB\u53D6\u548C\u67E5\u8BE2\u90FD\u5341\u5206\u65B9\u4FBF\u3002</p></li><li><p>\u5B58\u50A8\u7ED3\u6784\uFF1A\u5173\u7CFB\u578B\u6570\u636E\u5E93\u6309\u7167\u7ED3\u6784\u5316\u7684\u65B9\u6CD5\u5B58\u50A8\u6570\u636E\uFF0C\u6BCF\u4E2A\u6570\u636E\u8868\u90FD\u5FC5\u987B\u5BF9\u5404\u4E2A\u5B57\u6BB5\u5B9A\u4E49\u597D\uFF08\u4E5F\u5C31\u662F\u5148\u5B9A\u4E49\u597D\u8868\u7684\u7ED3\u6784\uFF09\uFF0C\u518D\u6839\u636E\u8868\u7684\u7ED3\u6784\u5B58\u5165\u6570\u636E\uFF0C\u8FD9\u6837\u505A\u7684\u597D\u5904\u5C31\u662F\u7531\u4E8E\u6570\u636E\u7684\u5F62\u5F0F\u548C\u5185\u5BB9\u5728\u5B58\u5165\u6570\u636E\u4E4B\u524D\u5C31\u5DF2\u7ECF\u5B9A\u4E49\u597D\u4E86\uFF0C\u6240\u4EE5\u6574\u4E2A\u6570\u636E\u8868\u7684\u53EF\u9760\u6027\u548C\u7A33\u5B9A\u6027\u90FD\u6BD4\u8F83\u9AD8\uFF0C\u4F46\u5E26\u6765\u7684\u95EE\u9898\u5C31\u662F\u4E00\u65E6\u5B58\u5165\u6570\u636E\u540E\uFF0C\u5982\u679C\u9700\u8981\u4FEE\u6539\u6570\u636E\u8868\u7684\u7ED3\u6784\u5C31\u4F1A\u5341\u5206\u56F0\u96BE\u3002</p></li><li><p>\u5B58\u50A8\u89C4\u8303\uFF1A\u5173\u7CFB\u578B\u6570\u636E\u5E93\u4E3A\u4E86\u907F\u514D\u91CD\u590D\u3001\u89C4\u8303\u5316\u6570\u636E\u4EE5\u53CA\u5145\u5206\u5229\u7528\u597D\u5B58\u50A8\u7A7A\u95F4\uFF0C\u628A\u6570\u636E\u6309\u7167\u6700\u5C0F\u5173\u7CFB\u8868\u7684\u5F62\u5F0F\u8FDB\u884C\u5B58\u50A8\uFF0C\u8FD9\u6837\u6570\u636E\u7BA1\u7406\u7684\u5C31\u53EF\u4EE5\u53D8\u5F97\u5F88\u6E05\u6670\u3001\u4E00\u76EE\u4E86\u7136\uFF0C\u5F53\u7136\u8FD9\u4E3B\u8981\u662F\u4E00\u5F20\u6570\u636E\u8868\u7684\u60C5\u51B5\u3002\u5982\u679C\u662F\u591A\u5F20\u8868\u60C5\u51B5\u5C31\u4E0D\u4E00\u6837\u4E86\uFF0C\u7531\u4E8E\u6570\u636E\u6D89\u53CA\u5230\u591A\u5F20\u6570\u636E\u8868\uFF0C\u6570\u636E\u8868\u4E4B\u95F4\u5B58\u5728\u7740\u590D\u6742\u7684\u5173\u7CFB\uFF0C\u968F\u7740\u6570\u636E\u8868\u6570\u91CF\u7684\u589E\u52A0\uFF0C\u6570\u636E\u7BA1\u7406\u4F1A\u8D8A\u6765\u8D8A\u590D\u6742\u3002</p></li><li><p>\u6269\u5C55\u65B9\u5F0F\uFF1A\u7531\u4E8E\u5173\u7CFB\u578B\u6570\u636E\u5E93\u5C06\u6570\u636E\u5B58\u50A8\u5728\u6570\u636E\u8868\u4E2D\uFF0C\u6570\u636E\u64CD\u4F5C\u7684\u74F6\u9888\u51FA\u73B0\u5728\u591A\u5F20\u6570\u636E\u8868\u7684\u64CD\u4F5C\u4E2D\uFF0C\u800C\u4E14\u6570\u636E\u8868\u8D8A\u591A\u8FD9\u4E2A\u95EE\u9898\u8D8A\u4E25\u91CD\uFF0C\u5982\u679C\u8981\u7F13\u89E3\u8FD9\u4E2A\u95EE\u9898\uFF0C\u53EA\u80FD\u63D0\u9AD8\u5904\u7406\u80FD\u529B\uFF0C\u4E5F\u5C31\u662F\u9009\u62E9\u901F\u5EA6\u66F4\u5FEB\u6027\u80FD\u66F4\u9AD8\u7684\u8BA1\u7B97\u673A\uFF0C\u8FD9\u6837\u7684\u65B9\u6CD5\u867D\u7136\u53EF\u4EE5\u4E00\u5B9A\u7684\u62D3\u5C55\u7A7A\u95F4\uFF0C\u4F46\u8FD9\u6837\u7684\u62D3\u5C55\u7A7A\u95F4\u4E00\u5B9A\u6709\u975E\u5E38\u6709\u9650\u7684\uFF0C\u4E5F\u5C31\u662F\u5173\u7CFB\u578B\u6570\u636E\u5E93\u53EA\u5177\u5907\u7EB5\u5411\u6269\u5C55\u80FD\u529B\u3002</p></li><li><p>\u67E5\u8BE2\u65B9\u5F0F\uFF1A\u5173\u7CFB\u578B\u6570\u636E\u5E93\u91C7\u7528\u7ED3\u6784\u5316\u67E5\u8BE2\u8BED\u8A00\uFF08\u5373SQL\uFF09\u6765\u5BF9\u6570\u636E\u5E93\u8FDB\u884C\u67E5\u8BE2\uFF0CSQL\u65E9\u5DF2\u83B7\u5F97\u4E86\u5404\u4E2A\u6570\u636E\u5E93\u5382\u5546\u7684\u652F\u6301\uFF0C\u6210\u4E3A\u6570\u636E\u5E93\u884C\u4E1A\u7684\u6807\u51C6\uFF0C\u5B83\u80FD\u591F\u652F\u6301\u6570\u636E\u5E93\u7684CRUD\uFF08\u589E\u52A0\uFF0C\u67E5\u8BE2\uFF0C\u66F4\u65B0\uFF0C\u5220\u9664\uFF09\u64CD\u4F5C\uFF0C\u5177\u6709\u975E\u5E38\u5F3A\u5927\u7684\u529F\u80FD\uFF0CSQL\u53EF\u4EE5\u91C7\u7528\u7C7B\u4F3C\u7D22\u5F15\u7684\u65B9\u6CD5\u6765\u52A0\u5FEB\u67E5\u8BE2\u64CD\u4F5C\u3002</p></li><li><p>\u89C4\u8303\u5316\uFF1A\u5728\u6570\u636E\u5E93\u7684\u8BBE\u8BA1\u5F00\u53D1\u8FC7\u7A0B\u4E2D\u5F00\u53D1\u4EBA\u5458\u901A\u5E38\u4F1A\u9762\u5BF9\u540C\u65F6\u9700\u8981\u5BF9\u4E00\u4E2A\u6216\u8005\u591A\u4E2A\u6570\u636E\u5B9E\u4F53\uFF08\u5305\u62EC\u6570\u7EC4\u3001\u5217\u8868\u548C\u5D4C\u5957\u6570\u636E\uFF09\u8FDB\u884C\u64CD\u4F5C\uFF0C\u8FD9\u6837\u5728\u5173\u7CFB\u578B\u6570\u636E\u5E93\u4E2D\uFF0C\u4E00\u4E2A\u6570\u636E\u5B9E\u4F53\u4E00\u822C\u9996\u5148\u8981\u5206\u5272\u6210\u591A\u4E2A\u90E8\u5206\uFF0C\u7136\u540E\u518D\u5BF9\u5206\u5272\u7684\u90E8\u5206\u8FDB\u884C\u89C4\u8303\u5316\uFF0C\u89C4\u8303\u5316\u4EE5\u540E\u518D\u5206\u522B\u5B58\u5165\u5230\u591A\u5F20\u5173\u7CFB\u578B\u6570\u636E\u8868\u4E2D\uFF0C\u8FD9\u662F\u4E00\u4E2A\u590D\u6742\u7684\u8FC7\u7A0B\u3002\u597D\u6D88\u606F\u662F\u968F\u7740\u8F6F\u4EF6\u6280\u672F\u7684\u53D1\u5C55\uFF0C\u76F8\u5F53\u591A\u7684\u8F6F\u4EF6\u5F00\u53D1\u5E73\u53F0\u90FD\u63D0\u4F9B\u4E00\u4E9B\u7B80\u5355\u7684\u89E3\u51B3\u65B9\u6CD5\uFF0C\u4F8B\u5982\uFF0C\u53EF\u4EE5\u5229\u7528ORM\u5C42\uFF08\u4E5F\u5C31\u662F\u5BF9\u8C61\u5173\u7CFB\u6620\u5C04\uFF09\u6765\u5C06\u6570\u636E\u5E93\u4E2D\u5BF9\u8C61\u6A21\u578B\u6620\u5C04\u5230\u57FA\u4E8ESQL\u7684\u5173\u7CFB\u578B\u6570\u636E\u5E93\u4E2D\u53BB\u4EE5\u53CA\u8FDB\u884C\u4E0D\u540C\u7C7B\u578B\u7CFB\u7EDF\u7684\u6570\u636E\u4E4B\u95F4\u7684\u8F6C\u6362\u3002</p></li><li><p>\u4E8B\u52A1\u6027\uFF1A\u5173\u7CFB\u578B\u6570\u636E\u5E93\u5F3A\u8C03ACID\u89C4\u5219\uFF08\u539F\u5B50\u6027\uFF08Atomicity\uFF09\u3001\u4E00\u81F4\u6027\uFF08Consistency\uFF09\u3001\u9694\u79BB\u6027\uFF08Isolation\uFF09\u3001\u6301\u4E45\u6027\uFF08Durability\uFF09\uFF09\uFF0C\u53EF\u4EE5\u6EE1\u8DB3\u5BF9\u4E8B\u52A1\u6027\u8981\u6C42\u8F83\u9AD8\u6216\u8005\u9700\u8981\u8FDB\u884C\u590D\u6742\u6570\u636E\u67E5\u8BE2\u7684\u6570\u636E\u64CD\u4F5C\uFF0C\u800C\u4E14\u53EF\u4EE5\u5145\u5206\u6EE1\u8DB3\u6570\u636E\u5E93\u64CD\u4F5C\u7684\u9AD8\u6027\u80FD\u548C\u64CD\u4F5C\u7A33\u5B9A\u6027\u7684\u8981\u6C42\u3002\u5E76\u4E14\u5173\u7CFB\u578B\u6570\u636E\u5E93\u5341\u5206\u5F3A\u8C03\u6570\u636E\u7684\u5F3A\u4E00\u81F4\u6027\uFF0C\u5BF9\u4E8E\u4E8B\u52A1\u7684\u64CD\u4F5C\u6709\u5F88\u597D\u7684\u652F\u6301\u3002\u5173\u7CFB\u578B\u6570\u636E\u5E93\u53EF\u4EE5\u63A7\u5236\u4E8B\u52A1\u539F\u5B50\u6027\u7EC6\u7C92\u5EA6\uFF0C\u5E76\u4E14\u4E00\u65E6\u64CD\u4F5C\u6709\u8BEF\u6216\u8005\u6709\u9700\u8981\uFF0C\u53EF\u4EE5\u9A6C\u4E0A\u56DE\u6EDA\u4E8B\u52A1\u3002</p></li><li><p>\u8BFB\u5199\u6027\u80FD\uFF1A\u5173\u7CFB\u578B\u6570\u636E\u5E93\u5341\u5206\u5F3A\u8C03\u6570\u636E\u7684\u4E00\u81F4\u6027\uFF0C\u5E76\u4E3A\u6B64\u964D\u4F4E\u8BFB\u5199\u6027\u80FD\u4ED8\u51FA\u4E86\u5DE8\u5927\u7684\u4EE3\u4EF7\uFF0C\u867D\u7136\u5173\u7CFB\u578B\u6570\u636E\u5E93\u5B58\u50A8\u6570\u636E\u548C\u5904\u7406\u6570\u636E\u7684\u53EF\u9760\u6027\u5F88\u4E0D\u9519\uFF0C\u4F46\u4E00\u65E6\u9762\u5BF9\u6D77\u91CF\u6570\u636E\u7684\u5904\u7406\u7684\u65F6\u5019\u6548\u7387\u5C31\u4F1A\u53D8\u5F97\u5F88\u5DEE\uFF0C\u7279\u522B\u662F\u9047\u5230\u9AD8\u5E76\u53D1\u8BFB\u5199\u7684\u65F6\u5019\u6027\u80FD\u5C31\u4F1A\u4E0B\u964D\u7684\u975E\u5E38\u5389\u5BB3\u3002</p></li><li><p>\u6388\u6743\u65B9\u5F0F\uFF1A\u5173\u7CFB\u578B\u6570\u636E\u5E93\u5E38\u89C1\u7684\u6709Oracle\uFF0CSQLServer\uFF0CDB2\uFF0CMysql\uFF0C\u9664\u4E86Mysql\u5927\u591A\u6570\u7684\u5173\u7CFB\u578B\u6570\u636E\u5E93\u5982\u679C\u8981\u4F7F\u7528\u90FD\u9700\u8981\u652F\u4ED8\u4E00\u7B14\u4EF7\u683C\u9AD8\u6602\u7684\u8D39\u7528\uFF0C\u5373\u4F7F\u662F\u514D\u8D39\u7684Mysql\u6027\u80FD\u4E5F\u53D7\u5230\u4E86\u8BF8\u591A\u7684\u9650\u5236\u3002</p></li></ol><h2 id="\u975E\u5173\u7CFB\u578B\u6570\u636E\u5E93" tabindex="-1"><a class="header-anchor" href="#\u975E\u5173\u7CFB\u578B\u6570\u636E\u5E93" aria-hidden="true">#</a> \u975E\u5173\u7CFB\u578B\u6570\u636E\u5E93</h2><p>\u975E\u5173\u7CFB\u578B\u6570\u636E\u5E93\u7684\u5B9E\u8D28\uFF1A\u975E\u5173\u7CFB\u578B\u6570\u636E\u5E93\u4EA7\u54C1\u662F\u4F20\u7EDF\u5173\u7CFB\u578B\u6570\u636E\u5E93\u7684\u529F\u80FD\u9609\u5272\u7248\u672C\uFF0C\u901A\u8FC7\u51CF\u5C11\u7528\u4E0D\u5230\u6216\u5F88\u5C11\u7528\u7684\u529F\u80FD\uFF0C\u6765\u5927\u5E45\u5EA6\u63D0\u9AD8\u4EA7\u54C1\u6027\u80FD\u3002</p><p>\u975E\u5173\u7CFB\u578B\u6570\u636E\u5E93\u7279\u70B9\uFF1A</p><p>\u5BF9\u4E8ENoSQL\u5E76\u6CA1\u6709\u4E00\u4E2A\u660E\u786E\u7684\u8303\u56F4\u548C\u5B9A\u4E49\uFF0C\u4F46\u662F\u4ED6\u4EEC\u90FD\u666E\u904D\u5B58\u5728\u4E0B\u9762\u4E00\u4E9B\u5171\u540C\u7279\u5F81\uFF1A</p><ol><li><p>\u6613\u6269\u5C55\uFF1ANoSQL\u6570\u636E\u5E93\u79CD\u7C7B\u7E41\u591A\uFF0C\u4F46\u662F\u4E00\u4E2A\u5171\u540C\u7684\u7279\u70B9\u90FD\u662F\u53BB\u6389\u5173\u7CFB\u6570\u636E\u5E93\u7684\u5173\u7CFB\u578B\u7279\u6027\u3002\u6570\u636E\u4E4B\u95F4\u65E0\u5173\u7CFB\uFF0C\u8FD9\u6837\u5C31\u975E\u5E38\u5BB9\u6613\u6269\u5C55\u3002\u65E0\u5F62\u4E4B\u95F4\uFF0C\u5728\u67B6\u6784\u7684\u5C42\u9762\u4E0A\u5E26\u6765\u4E86\u53EF\u6269\u5C55\u7684\u80FD\u529B\u3002[</p></li><li><p>\u5927\u6570\u636E\u91CF\uFF0C\u9AD8\u6027\u80FD\uFF1ANoSQL\u6570\u636E\u5E93\u90FD\u5177\u6709\u975E\u5E38\u9AD8\u7684\u8BFB\u5199\u6027\u80FD\uFF0C\u5C24\u5176\u5728\u5927\u6570\u636E\u91CF\u4E0B\uFF0C\u540C\u6837\u8868\u73B0\u4F18\u79C0\u3002\u8FD9\u5F97\u76CA\u4E8E\u5B83\u7684\u65E0\u5173\u7CFB\u6027\uFF0C\u6570\u636E\u5E93\u7684\u7ED3\u6784\u7B80\u5355\u3002\u4E00\u822CMySQL\u4F7F\u7528Query Cache\u3002NoSQL\u7684Cache\u662F\u8BB0\u5F55\u7EA7\u7684\uFF0C\u662F\u4E00\u79CD\u7EC6\u7C92\u5EA6\u7684Cache\uFF0C\u6240\u4EE5NoSQL\u5728\u8FD9\u4E2A\u5C42\u9762\u4E0A\u6765\u8BF4\u6027\u80FD\u5C31\u8981\u9AD8\u5F88\u591A\u3002</p></li><li><p>\u7075\u6D3B\u7684\u6570\u636E\u6A21\u578B\uFF1ANoSQL\u65E0\u987B\u4E8B\u5148\u4E3A\u8981\u5B58\u50A8\u7684\u6570\u636E\u5EFA\u7ACB\u5B57\u6BB5\uFF0C\u968F\u65F6\u53EF\u4EE5\u5B58\u50A8\u81EA\u5B9A\u4E49\u7684\u6570\u636E\u683C\u5F0F\u3002\u800C\u5728\u5173\u7CFB\u6570\u636E\u5E93\u91CC\uFF0C\u589E\u5220\u5B57\u6BB5\u662F\u4E00\u4EF6\u975E\u5E38\u9EBB\u70E6\u7684\u4E8B\u60C5\u3002\u5982\u679C\u662F\u975E\u5E38\u5927\u6570\u636E\u91CF\u7684\u8868\uFF0C\u589E\u52A0\u5B57\u6BB5\u7B80\u76F4\u5C31\u662F\u2014\u2014\u4E2A\u5669\u68A6\u3002\u8FD9\u70B9\u5728\u5927\u6570\u636E\u91CF\u7684Web 2.0\u65F6\u4EE3\u5C24\u5176\u660E\u663E\u3002</p></li><li><p>\u9AD8\u53EF\u7528\uFF1ANoSQL\u5728\u4E0D\u592A\u5F71\u54CD\u6027\u80FD\u7684\u60C5\u51B5\uFF0C\u5C31\u53EF\u4EE5\u65B9\u4FBF\u5730\u5B9E\u73B0\u9AD8\u53EF\u7528\u7684\u67B6\u6784\u3002\u6BD4\u5982Cassandra\u3001HBase\u6A21\u578B\uFF0C\u901A\u8FC7\u590D\u5236\u6A21\u578B\u4E5F\u80FD\u5B9E\u73B0\u9AD8\u53EF\u7528\u3002</p></li></ol><h2 id="\u4E24\u79CD\u6570\u636E\u5E93\u6BD4\u8F83" tabindex="-1"><a class="header-anchor" href="#\u4E24\u79CD\u6570\u636E\u5E93\u6BD4\u8F83" aria-hidden="true">#</a> \u4E24\u79CD\u6570\u636E\u5E93\u6BD4\u8F83</h2><ol><li><p>\u6210\u672C:Nosql\u6570\u636E\u5E93\u5F88\u5BB9\u6613\u90E8\u7F72\uFF0C\u57FA\u672C\u4E0A\u662F\u5F00\u6E90\u8F6F\u4EF6\uFF0C\u65E0\u9700\u50CFOracle\u90A3\u6837\u82B1\u8D39\u5927\u91CF\u6210\u672C\u8D2D\u4E70\uFF0C\u6BD4\u5173\u7CFB\u6570\u636E\u5E93\u4FBF\u5B9C\u3002</p></li><li><p>\u67E5\u8BE2\u901F\u5EA6:Nosql\u6570\u636E\u5E93\u5C06\u6570\u636E\u5B58\u50A8\u5728\u9AD8\u901F\u7F13\u5B58\u4E2D\uFF0C\u4E0D\u9700\u8981\u5BF9SQL\u5C42\u8FDB\u884C\u5206\u6790\u3002\u5173\u7CFB\u6570\u636E\u5E93\u5728\u786C\u76D8\u4E0A\u5B58\u50A8\u6570\u636E\uFF0C\u81EA\u7136\u7684\u67E5\u8BE2\u901F\u5EA6\u8FDC\u6BD4Nosql\u6570\u636E\u5E93\u6162\u3002</p></li><li><p>\u5B58\u50A8\u6570\u636E\u7684\u683C\u5F0F\uFF1ANosql\u7684\u5B58\u50A8\u683C\u5F0F\u662Fkey,value\u5F62\u5F0F\u3001\u6587\u6863\u5F62\u5F0F\u3001\u56FE\u7247\u5F62\u5F0F\u7B49\u7B49\uFF0C\u6240\u4EE5\u53EF\u4EE5\u5B58\u50A8\u57FA\u7840\u7C7B\u578B\u4EE5\u53CA\u5BF9\u8C61\u6216\u8005\u662F\u96C6\u5408\u7B49\u5404\u79CD\u683C\u5F0F\uFF0C\u800C\u6570\u636E\u5E93\u5219\u53EA\u652F\u6301\u57FA\u7840\u7C7B\u578B\u3002</p></li><li><p>\u6269\u5C55\u6027\uFF1A\u5173\u7CFB\u578B\u6570\u636E\u5E93\u6709\u7C7B\u4F3Cjoin\u8FD9\u6837\u7684\u591A\u8868\u67E5\u8BE2\u673A\u5236\u7684\u9650\u5236\u5BFC\u81F4\u6269\u5C55\u5F88\u8270\u96BE\u3002Nosql\u57FA\u4E8E\u952E\u503C\u5BF9\uFF0C\u6570\u636E\u4E4B\u95F4\u6CA1\u6709\u8026\u5408\u6027\uFF0C\u6240\u4EE5\u975E\u5E38\u5BB9\u6613\u6C34\u5E73\u6269\u5C55\u3002</p></li><li><p>\u6301\u4E45\u5B58\u50A8\uFF1ANosql\u4E0D\u9002\u7528\u4E8E\u6301\u4E45\u5B58\u50A8\uFF0C\u6D77\u91CF\u6570\u636E\u7684\u6301\u4E45\u5B58\u50A8\uFF0C\u8FD8\u662F\u9700\u8981\u5173\u7CFB\u578B\u6570\u636E\u5E93</p></li><li><p>\u6570\u636E\u4E00\u81F4\u6027\uFF1A\u975E\u5173\u7CFB\u6570\u636E\u5E93\u901A\u5E38\u5F3A\u8C03\u6570\u636E\u7684\u6700\u7EC8\u4E00\u81F4\u6027\uFF0C\u800C\u4E0D\u662F\u50CF\u5173\u7CFB\u6570\u636E\u5E93\u90A3\u6837\u5F3A\u70C8\u7684\u6570\u636E\u4E00\u81F4\u6027\uFF0C\u4EE5\u53CA\u4ECE\u975E\u5173\u7CFB\u6570\u636E\u5E93\u8BFB\u53D6\u7684\u6570\u636E\u53EF\u80FD\u4ECD\u5904\u4E8E\u4E2D\u95F4\u72B6\u6001\uFF0CNosql\u4E0D\u63D0\u4F9B\u5BF9\u4E8B\u52A1\u7684\u5904\u7406\u3002</p></li></ol>',11),s={class:"custom-container tip"},c=e("p",{class:"custom-container-title"},"\u51FA\u5904",-1),_=a("\u4EE5\u4E0A\u4E09\u70B9\u5747\u6765\u6E90\u4E8E\u534E\u4E3A\u4E91\u793E\u533A"),N={href:"https://www.huaweicloud.com/zhishi/1592288147096.html",target:"_blank",rel:"noopener noreferrer"},f=a("https://www.huaweicloud.com/zhishi/1592288147096.html"),u=o('<h2 id="\u6570\u636E\u5E93\u8303\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u6570\u636E\u5E93\u8303\u5F0F" aria-hidden="true">#</a> \u6570\u636E\u5E93\u8303\u5F0F</h2><h3 id="\u6570\u636E\u5E93\u8303\u5F0F\u7684\u4F5C\u7528" tabindex="-1"><a class="header-anchor" href="#\u6570\u636E\u5E93\u8303\u5F0F\u7684\u4F5C\u7528" aria-hidden="true">#</a> \u6570\u636E\u5E93\u8303\u5F0F\u7684\u4F5C\u7528</h3><p>\u6570\u636E\u5E93\u8303\u5F0F\u4E3B\u8981\u662F\u4E3A\u89E3\u51B3\u5173\u7CFB\u6570\u636E\u5E93\u4E2D\u6570\u636E\u5197\u4F59\u3001\u66F4\u65B0\u5F02\u5E38\u3001\u63D2\u5165\u5F02\u5E38\u3001\u5220\u9664\u5F02\u5E38\u95EE\u9898\u800C\u5F15\u5165\u7684\u8BBE\u8BA1\u7406\u5FF5\u3002\u7B80\u5355\u6765\u8BF4\uFF0C\u6570\u636E\u5E93\u8303\u5F0F\u53EF\u4EE5\u907F\u514D\u6570\u636E\u5197\u4F59\uFF0C\u51CF\u5C11\u6570\u636E\u5E93\u7684\u5B58\u50A8\u7A7A\u95F4\uFF0C\u5E76\u4E14\u51CF\u8F7B\u7EF4\u62A4\u6570\u636E\u5B8C\u6574\u6027\u7684\u6210\u672C\u3002\u662F\u5173\u7CFB\u6570\u636E\u5E93\u6838\u5FC3\u7684\u6280\u672F\u4E4B\u4E00\uFF0C\u4E5F\u662F\u4ECE\u4E8B\u6570\u636E\u5E93\u5F00\u53D1\u4EBA\u5458\u5FC5\u5907\u77E5\u8BC6\u3002</p><h3 id="\u6570\u636E\u5E93\u8303\u5F0F\u5206\u7C7B\u4ECB\u7ECD" tabindex="-1"><a class="header-anchor" href="#\u6570\u636E\u5E93\u8303\u5F0F\u5206\u7C7B\u4ECB\u7ECD" aria-hidden="true">#</a> \u6570\u636E\u5E93\u8303\u5F0F\u5206\u7C7B\u4ECB\u7ECD</h3><p>\u8303\u5F0F\u662F\u8BC4\u4EF7\u6570\u636E\u5E93\u6A21\u5F0F\u89C4\u8303\u5316\u7A0B\u5EA6\u4ECE\u4F4E\u5230\u9AD8\u4E3B\u8981\u6709\uFF1A1NF\u30012NF\u30013Nf\u3001BCNF\u30014NF\u30015NF\u3002</p><h4 id="_1nf-\u7B2C\u4E00\u8303\u5F0F-\u4E0D\u53EF\u62C6\u5206" tabindex="-1"><a class="header-anchor" href="#_1nf-\u7B2C\u4E00\u8303\u5F0F-\u4E0D\u53EF\u62C6\u5206" aria-hidden="true">#</a> 1NF \u7B2C\u4E00\u8303\u5F0F\uFF08\u4E0D\u53EF\u62C6\u5206\uFF09</h4><p>\u5F3A\u8C03\u5C5E\u6027\u7684\u539F\u5B50\u6027\u7EA6\u675F\uFF0C\u8981\u6C42\u5C5E\u6027\u5177\u6709\u539F\u5B50\u6027\uFF0C\u4E0D\u53EF\u518D\u5206\u89E3\u3002</p><p>\u4E3E\u4F8B\uFF1A</p><p>\u5B66\u751F\u8868(\u5B66\u53F7\u3001\u59D3\u540D\u3001\u5E74\u9F84\u3001\u6027\u522B\u3001\u5730\u5740)\u3002\u5730\u5740\u53EF\u4EE5\u7EC6\u5206\u4E3A\u56FD\u5BB6\u3001\u7701\u4EFD\u3001\u57CE\u5E02\u3001\u5E02\u533A\u3001\u8857\u9053\uFF0C\u90A3\u4E48\u8BE5\u6A21\u5F0F\u5C31\u6CA1\u6709\u8FBE\u5230\u7B2C\u4E00\u8303\u5F0F\u3002</p><p>\u7B2C\u4E00\u8303\u5F0F\u5B58\u5728\u95EE\u9898\uFF1A\u5197\u4F59\u5EA6\u5927\u3001\u4F1A\u5F15\u8D77\u4FEE\u6539\u64CD\u4F5C\u7684\u4E0D\u4E00\u81F4\u6027\u3001\u6570\u636E\u63D2\u5165\u5F02\u5E38\u3001\u6570\u636E\u5220\u9664\u5F02\u5E38\u3002</p><h4 id="_2nf-\u7B2C\u4E8C\u8303\u5F0F-\u503C\u5FC5\u987B\u5B8C\u5168\u4F9D\u8D56\u4E3B\u952E" tabindex="-1"><a class="header-anchor" href="#_2nf-\u7B2C\u4E8C\u8303\u5F0F-\u503C\u5FC5\u987B\u5B8C\u5168\u4F9D\u8D56\u4E3B\u952E" aria-hidden="true">#</a> 2NF \u7B2C\u4E8C\u8303\u5F0F\uFF08\u503C\u5FC5\u987B\u5B8C\u5168\u4F9D\u8D56\u4E3B\u952E\uFF09</h4><p>\u7B2C\u4E8C\u8303\u5F0F\uFF0C\u5F3A\u8C03\u8BB0\u5F55\u7684\u552F\u4E00\u6027\u7EA6\u675F\uFF0C\u6570\u636E\u8868\u5FC5\u987B\u6709\u4E00\u4E2A\u4E3B\u952E\uFF0C\u5E76\u4E14\u6CA1\u6709\u5305\u542B\u5728\u4E3B\u952E\u4E2D\u7684\u5217\u5FC5\u987B\u5B8C\u5168\u4F9D\u8D56\u4E8E\u4E3B\u952E\uFF0C\u800C\u4E0D\u80FD\u53EA\u4F9D\u8D56\u4E8E\u4E3B\u952E\u7684\u4E00\u90E8\u5206\u3002</p><p>\u4E3E\u4F8B\uFF1A</p><p>\u7248\u672C\u8868(\u7248\u672C\u7F16\u7801\uFF0C\u7248\u672C\u540D\u79F0\uFF0C\u4EA7\u54C1\u7F16\u7801\uFF0C\u4EA7\u54C1\u540D\u79F0)\uFF0C\u5176\u4E2D\u4E3B\u952E\u662F(\u7248\u672C\u7F16\u7801\uFF0C\u4EA7\u54C1\u7F16\u7801)\uFF0C\u8FD9\u4E2A\u573A\u666F\u4E2D\uFF0C\u6570\u636E\u5E93\u8BBE\u8BA1\u5E76\u4E0D\u7B26\u5408\u7B2C\u4E8C\u8303\u5F0F\uFF0C\u56E0\u4E3A\u4EA7\u54C1\u540D\u79F0\u53EA\u4F9D\u8D56\u4E8E\u4EA7\u54C1\u7F16\u7801\u3002\u5B58\u5728\u90E8\u5206\u4F9D\u8D56\u3002\u6240\u4EE5\uFF0C\u4E3A\u4E86\u4F7F\u5176\u6EE1\u8DB3\u7B2C\u4E8C\u8303\u5F0F\uFF0C\u53EF\u4EE5\u6539\u9020\u6210\u4E24\u4E2A\u8868\uFF1A\u7248\u672C\u8868(\u7248\u672C\u7F16\u7801\uFF0C\u4EA7\u54C1\u7F16\u7801)\u548C\u4EA7\u54C1\u8868(\u4EA7\u54C1\u7F16\u7801\uFF0C\u4EA7\u54C1\u540D\u79F0)</p><h4 id="_3nf-\u7B2C\u4E09\u8303\u5F0F-\u6D88\u9664\u90E8\u5206\u4F9D\u8D56\u548C\u4F20\u9012\u4F9D\u8D56" tabindex="-1"><a class="header-anchor" href="#_3nf-\u7B2C\u4E09\u8303\u5F0F-\u6D88\u9664\u90E8\u5206\u4F9D\u8D56\u548C\u4F20\u9012\u4F9D\u8D56" aria-hidden="true">#</a> 3NF \u7B2C\u4E09\u8303\u5F0F\uFF08\u6D88\u9664\u90E8\u5206\u4F9D\u8D56\u548C\u4F20\u9012\u4F9D\u8D56\uFF09</h4><p>\u7B2C\u4E09\u8303\u5F0F\uFF0C\u5F3A\u8C03\u6570\u636E\u5C5E\u6027\u5197\u4F59\u6027\u7684\u7EA6\u675F\uFF0C\u4E5F\u5C31\u662F\u975E\u4E3B\u952E\u5217\u5FC5\u987B\u76F4\u63A5\u4F9D\u8D56\u4E8E\u4E3B\u952E\u3002\u4E5F\u5C31\u662F\u6D88\u9664\u4E86\u975E\u4E3B\u5C5E\u6027\u5BF9\u7801\u7684\u4F20\u9012\u51FD\u6570\u4F9D\u8D56\u3002</p><p>\u4E3E\u4F8B\uFF1A</p><p>\u8BA2\u5355\u8868(\u8BA2\u5355\u7F16\u7801\uFF0C\u987E\u5BA2\u7F16\u7801\uFF0C\u987E\u5BA2\u540D\u79F0)\uFF0C\u5176\u4E2D\u4E3B\u952E\u662F(\u8BA2\u5355\u7F16\u7801)\uFF0C\u8FD9\u4E2A\u573A\u666F\u4E2D\uFF0C\u987E\u5BA2\u7F16\u7801\u3001\u987E\u5BA2\u540D\u79F0\u90FD\u5B8C\u5168\u4F9D\u8D56\u4E8E\u4E3B\u952E\uFF0C\u56E0\u6B64\u7B26\u5408\u7B2C\u4E8C\u8303\u5F0F\uFF0C\u4F46\u987E\u5BA2\u540D\u79F0\u4F9D\u8D56\u4E8E\u987E\u5BA2\u7F16\u7801\uFF0C\u4ECE\u800C\u95F4\u63A5\u4F9D\u8D56\u4E8E\u4E3B\u952E\uFF0C\u6240\u4EE5\u4E0D\u80FD\u6EE1\u8DB3\u7B2C\u4E09\u8303\u5F0F\u3002\u5982\u679C\u8981\u6EE1\u8DB3\u7B2C\u4E09\u8303\u5F0F\uFF0C\u9700\u8981\u62C6\u5206\u4E3A\u4E24\u4E2A\u8868\uFF1A\u8BA2\u5355\u8868(\u8BA2\u5355\u7F16\u7801\uFF0C\u987E\u5BA2\u7F16\u7801)\u548C\u987E\u5BA2\u8868(\u987E\u5BA2\u7F16\u7801\uFF0C\u987E\u5BA2\u540D\u79F0)\u3002</p><p>\u8BF4\u660E\uFF1A3NF\u7684\u6A21\u5F0F\u80AF\u5B9A\u6EE1\u8DB32NF\u3002\u4EA7\u751F\u5197\u4F59\u548C\u5F02\u5E38\u7684\u4E24\u4E2A\u91CD\u8981\u539F\u56E0\u662F\u90E8\u5206\u4F9D\u8D56\u548C\u4F20\u9012\u4F9D\u8D56\u30023NF\u6A21\u5F0F\u4E2D\u4E0D\u5B58\u5728\u975E\u4E3B\u5C5E\u6027\u5BF9\u7801\u7684\u90E8\u5206\u51FD\u6570\u4F9D\u8D56\u548C\u4F20\u9012\u51FD\u6570\u4F9D\u8D56\uFF0C\u6027\u80FD\u8F83\u597D\u30021NF\u30012NF\u4E00\u822C\u4E0D\u9002\u5408\u4F5C\u4E3A\u6570\u636E\u5E93\u6A21\u5F0F\uFF0C\u901A\u5E38\u9700\u8981\u8F6C\u6362\u4E3A3NF\u6216\u8005\u66F4\u9AD8\u7EA7\u522B\u7684\u8303\u5F0F\uFF0C\u8FD9\u79CD\u53D8\u6362\u8FC7\u7A0B\u79F0\u4E3A\u5173\u7CFB\u6A21\u5F0F\u89C4\u8303\u5316\u5904\u7406\u3002</p><h4 id="bcnf-bovce-codd-normal-form-\u5DF4\u514B\u65AF\u8303\u5F0F-\u6D88\u9664\u4E3B\u952E\u95F4\u7684\u4F9D\u8D56" tabindex="-1"><a class="header-anchor" href="#bcnf-bovce-codd-normal-form-\u5DF4\u514B\u65AF\u8303\u5F0F-\u6D88\u9664\u4E3B\u952E\u95F4\u7684\u4F9D\u8D56" aria-hidden="true">#</a> BCNF(Bovce Codd Normal Form \u5DF4\u514B\u65AF\u8303\u5F0F)\uFF08\u6D88\u9664\u4E3B\u952E\u95F4\u7684\u4F9D\u8D56\uFF09</h4><p>\u5C5E\u4E8E\u4FEE\u6B63\u7684\u7B2C\u4E09\u8303\u5F0F\uFF0C\u662F\u9632\u6B62\u4E3B\u952E\u7684\u67D0\u4E00\u5217\u4F1A\u4F9D\u8D56\u4E8E\u4E3B\u952E\u7684\u5176\u4ED6\u5217\u3002\u5F533NF\u6D88\u9664\u4E86\u4E3B\u5C5E\u6027\u5BF9\u7801\u7684\u90E8\u5206\u51FD\u6570\u4F9D\u8D56\u548C\u4F20\u9012\u51FD\u6570\u4F9D\u8D56\u79F0\u4E3ABCNF\u3002</p><p>\u7279\u6027\uFF1A</p><p>1\u3001\u6240\u6709\u4E3B\u5C5E\u6027\u5BF9\u6BCF\u4E00\u4E2A\u7801\u90FD\u662F\u5B8C\u5168\u51FD\u6570\u4F9D\u8D56</p><p>2\u3001\u6240\u6709\u4E3B\u5C5E\u6027\u5BF9\u6BCF\u4E00\u4E2A\u4E0D\u5305\u542B\u5B83\u7684\u7801\uFF0C\u4E5F\u662F\u5B8C\u5168\u51FD\u6570\u4F9D\u8D56</p><p>3\u3001\u6CA1\u6709\u4EFB\u4F55\u5C5E\u6027\u5B8C\u5168\u51FD\u6570\u4F9D\u8D56\u4E0E\u975E\u7801\u7684\u4EFB\u4F55\u4E00\u7EC4\u5C5E\u6027</p><p>\u4E3E\u4F8B\uFF1A\u5E93\u5B58\u8868(\u4ED3\u5E93\u540D\uFF0C\u7BA1\u7406\u5458\u540D\uFF0C\u5546\u54C1\u540D\uFF0C\u6570\u91CF)\uFF0C\u4E3B\u952E\u4E3A(\u4ED3\u5E93\u540D\uFF0C\u7BA1\u7406\u5458\u540D\uFF0C\u5546\u54C1\u540D)\uFF0C\u8FD9\u662F\u6EE1\u8DB3\u524D\u9762\u4E09\u4E2A\u8303\u5F0F\u7684\uFF0C\u4F46\u662F\u4ED3\u5E93\u540D\u548C\u7BA1\u7406\u5458\u540D\u4E4B\u95F4\u5B58\u5728\u4F9D\u8D56\u5173\u7CFB\uFF0C\u56E0\u6B64\u5220\u9664\u67D0\u4E00\u4E2A\u4ED3\u5E93\uFF0C\u4F1A\u5BFC\u81F4\u7BA1\u7406\u5458\u4E5F\u88AB\u5220\u9664\uFF0C\u8FD9\u6837\u5C31\u4E0D\u6EE1\u8DB3BCNF\u3002</p><h4 id="_4nf-\u7B2C\u56DB\u8303\u5F0F-\u6D88\u9664\u591A\u503C\u4F9D\u8D56" tabindex="-1"><a class="header-anchor" href="#_4nf-\u7B2C\u56DB\u8303\u5F0F-\u6D88\u9664\u591A\u503C\u4F9D\u8D56" aria-hidden="true">#</a> 4NF \u7B2C\u56DB\u8303\u5F0F\uFF08\u6D88\u9664\u591A\u503C\u4F9D\u8D56\uFF09</h4><p>\u975E\u4E3B\u5C5E\u6027\u4E0D\u5E94\u8BE5\u6709\u591A\u503C\u3002\u5982\u679C\u6709\u591A\u503C\u5C31\u8FDD\u53CD\u4E86\u7B2C\u56DB\u8303\u5F0F\u30024NF\u662F\u9650\u5236\u5173\u7CFB\u6A21\u5F0F\u7684\u5C5E\u6027\u95F4\u4E0D\u5141\u8BB8\u6709\u975E\u5E73\u51E1\u4E14\u975E\u51FD\u6570\u4F9D\u8D56\u7684\u591A\u503C\u4F9D\u8D56\u3002</p><p>\u4E3E\u4F8B\uFF1A\u7528\u6237\u8054\u7CFB\u65B9\u5F0F\u8868(\u7528\u6237id\uFF0C\u56FA\u5B9A\u7535\u8BDD\uFF0C\u79FB\u52A8\u7535\u8BDD)\uFF0C\u5176\u4E2D\u7528\u6237id\u662F\u4E3B\u952E\uFF0C\u8FD9\u4E2A\u6EE1\u8DB3\u4E86BCNF,\u4F46\u662F\u4E00\u4E2A\u7528\u6237\u6709\u53EF\u80FD\u4F1A\u6709\u591A\u4E2A\u56FA\u5B9A\u7535\u8BDD\u6216\u8005\u591A\u4E2A\u79FB\u52A8\u7535\u8BDD\uFF0C\u90A3\u4E48\u8FD9\u79CD\u8BBE\u8BA1\u5C31\u4E0D\u5408\u7406\uFF0C\u5E94\u8BE5\u6539\u4E3A(\u7528\u6237id\uFF0C\u8054\u7CFB\u65B9\u5F0F\u7C7B\u578B\uFF0C\u7535\u8BDD\u53F7\u7801)\u3002</p><p>\u8BF4\u660E\uFF1A\u5982\u679C\u53EA\u8003\u8651\u51FD\u6570\u4F9D\u8D56\uFF0C\u5173\u7CFB\u6A21\u5F0F\u89C4\u8303\u5316\u7A0B\u5EA6\u6700\u9AD8\u7684\u8303\u5F0F\u662FBCNF;\u5982\u679C\u8003\u8651\u591A\u503C\u4F9D\u8D56\u5219\u662F4NF\u3002</p><h4 id="_5nf-\u7B2C\u4E94\u8303\u5F0F" tabindex="-1"><a class="header-anchor" href="#_5nf-\u7B2C\u4E94\u8303\u5F0F" aria-hidden="true">#</a> 5NF \u7B2C\u4E94\u8303\u5F0F</h4><p>\u7B2C\u4E94\u8303\u5F0F\u5C5E\u4E8E\u6700\u7EC8\u8303\u5F0F\uFF0C\u6D88\u9664\u4E864NF\u4E2D\u7684\u8FDE\u63A5\u4F9D\u8D56\uFF0C\u7B2C\u4E94\u8303\u5F0F\u9700\u8981\u6EE1\u8DB3\u4EE5\u4E0B\u8981\u6C42\uFF1A</p><p>1\u3001\u5FC5\u987B\u6EE1\u8DB3\u7B2C\u56DB\u8303\u5F0F</p><p>2\u3001\u8868\u5FC5\u987B\u53EF\u4EE5\u5206\u89E3\u4E3A\u8F83\u5C0F\u7684\u8868\uFF0C\u9664\u975E\u90A3\u4E9B\u8868\u5728\u903B\u8F91\u4E0A\u62E5\u6709\u4E0E\u539F\u59CB\u8868\u76F8\u540C\u7684\u4E3B\u952E\u3002</p><p>\u4E00\u822C\u5B9E\u9645\u5E94\u7528\u4E2D\u4E0D\u5FC5\u8003\u8651\u7B2C\u4E94\u8303\u5F0F\u3002</p>',35),m={class:"custom-container tip"},F=e("p",{class:"custom-container-title"},"\u8303\u5F0F\u6765\u6E90",-1),b=a("\u8303\u5F0F\u4ECB\u7ECD\u6765\u6E90\u4E8E\u4ECA\u65E5\u5934\u6761"),x={href:"https://www.51cto.com/article/632448.html",target:"_blank",rel:"noopener noreferrer"},L=a("https://www.51cto.com/article/632448.html");function S(C,Q){const i=t("ExternalLinkIcon");return h(),n("div",null,[d,e("div",s,[c,e("p",null,[_,e("a",N,[f,r(i)])])]),u,e("div",m,[F,e("p",null,[b,e("a",x,[L,r(i)])])])])}var B=p(l,[["render",S],["__file","index.html.vue"]]);export{B as default};
